function ModelAVOUncertainty2024c%	rockAVO Uncertainty%	Uses the Soft Sand/Shale Model%	WaterSalinity			ppm%	OilAPI					API%	GOR						l/l%	GasGravity				Standard Units%	Pressure				MPa%	Temperature				CelsiusCoordinationShale=7;CoordinationSand=6;%	Fluid property calculations	prompt={'Water Salinity (ppm)','Oil Garvity (API)', ...	'Gas Gravity (Specific)','GOR (l/l)', ...	'Pore Pressure (MPa)','Temperature (C)','Coordination Sand','Coordination Shale','Sw Oil Sand','Sw Gas Sand'};	defans={'40000','35','0.65','300','30','70','7','10','0.4','0.2'};	getpar=inputdlg(prompt,'Pore Fluid Properties and Conditions',1,defans);	for k=1:length(getpar), param(k)=str2num(getpar{k}); end;	WaterSalinity=param(1); OilAPI=param(2); GasGravity=param(3);	GOR=param(4); Pressure=param(5); Temperature=param(6); CoordinationSand=param(7); CoordinationShale=param(8);    SwOilSand=param(9); SwGasSand=param(10);	[RHOw,Kw,Vpw,RHOo,Ko,Vpo,RHOg,Kg,Vpg]= ...	BatzleWangNew(WaterSalinity,OilAPI,GasGravity,GOR,0,Pressure,Temperature);	figure	set(gcf,'Position',[50 250 850 600])							%	Porosity-Clay Setup	p=[.0:.01:.40]'; c=[0:.01:1]'; [Porosity ClayContent]=meshgrid(p,c);	subplot('position',[.39 .56 .22 .37])	pcolor(Porosity,ClayContent,1-ClayContent); shading interp; 	axis([0 .4001 0 1.0001]);	box on; set(gca,'fontname','helvetica','fontsize',8);	set(gca,'xtick',[0:.1:2]); set(gca,'ytick',[0:.1:2]);	xlabel('TOTAL POROSITY','fontname','helvetica','fontsize',8);	ylabel('CLAY CONTENT','fontname','helvetica','fontsize',8);	hold	for lll=1:1:11		plot([0 1]',(lll-1)*[.1 .1]','k')	end	for lll=1:1:11		plot((lll-1)*[.05 .05]',[0 1]','k')	end	title('POROSITY AND CLAY RANGES','fontname','helvetica','fontsize',11,'color','k');	text(.15,.10,'SAND','fontname','helvetica','fontsize',16,'fontweight','bold','color','w');	text(.15,.7,'SHALE','fontname','helvetica','fontsize',16,'fontweight','bold','color','k');	text(.01,.9,'SELECT SAND RANGE FIRST','fontname','helvetica','fontsize',12,'color','w');%	Select Sand Property Range	a1=ginput(1);	plot([a1(1,1) a1(1,1)],[a1(1,2) a1(1,2)],'w.','linewidth',2)	a2=ginput(1);	plot([a2(1,1) a2(1,1)],[a2(1,2) a2(1,2)],'w.','linewidth',2)	plot([a1(1,1) a2(1,1)],[a1(1,2) a1(1,2)],'w-','linewidth',2)	plot([a1(1,1) a2(1,1)],[a2(1,2) a2(1,2)],'w-','linewidth',2)	plot([a1(1,1) a1(1,1)],[a1(1,2) a2(1,2)],'w-','linewidth',2)	plot([a2(1,1) a2(1,1)],[a1(1,2) a2(1,2)],'w-','linewidth',2)%	Select Shale Property Range	b1=ginput(1);	plot([b1(1,1) b1(1,1)],[b1(1,2) b1(1,2)],'k.','linewidth',2)	b2=ginput(1);	plot([b2(1,1) b2(1,1)],[b2(1,2) b2(1,2)],'k.','linewidth',2)	plot([b1(1,1) b2(1,1)],[b1(1,2) b1(1,2)],'k-','linewidth',2)	plot([b1(1,1) b2(1,1)],[b2(1,2) b2(1,2)],'k-','linewidth',2)	plot([b1(1,1) b1(1,1)],[b1(1,2) b2(1,2)],'k-','linewidth',2)	plot([b2(1,1) b2(1,1)],[b1(1,2) b2(1,2)],'k-','linewidth',2)	%	Porosity and clay range in sand	pSS1=min(a1(1,1),a2(1,1));	pSS2=max(a1(1,1),a2(1,1));	cSS1=min(a1(1,2),a2(1,2));	cSS2=max(a1(1,2),a2(1,2));%	Porosity and clay arrays in sand	pSS=[pSS1:(pSS2-pSS1)/20:pSS2]';	cSS=[cSS1:(cSS2-cSS1)/20:cSS2]';	[ClaySS PhiSS]=meshgrid(cSS,pSS);%	Fluid properties for 20% gas saturation	Sw=SwGasSand; So=0.0; Sg=1-Sw-So;	Kf=1./(So./Ko+Sg./Kg+Sw./Kw);	RHOf=So.*RHOo+Sg.*RHOg+Sw.*RHOw;    [Sw Sg So Kf RHOf]%	Gas Sand Elastic Properties	[VpGSS,VsGSS,IpGSS,PRGSS,RHOBGSS,PhiGSS]=SoftSandNew(Kf,RHOf,PhiSS,1-ClaySS,ClaySS,0,0,0,16,.4,CoordinationSand,1);%	Fluid properties for oil sand	Sw=SwOilSand; Sg=0.0; So=1-Sw-Sg;	Kf=1./(So./Ko+Sg./Kg+Sw./Kw);	RHOf=So.*RHOo+Sg.*RHOg+Sw.*RHOw;    [Sw Sg So Kf RHOf]%	Oil Sand Elastic Properties	[VpOSS,VsOSS,IpOSS,PROSS,RHOBOSS,PhiOSS]=SoftSandNew(Kf,RHOf,PhiSS,1-ClaySS,ClaySS,0,0,0,16,.4,CoordinationSand,1);	%	Fluid properties for wet sand	So=0.0; Sg=0.0; Sw=1-So-Sg;	Kf=1./(So./Ko+Sg./Kg+Sw./Kw);	RHOf=So.*RHOo+Sg.*RHOg+Sw.*RHOw;%	Wet Sandstone	[VpWSS,VsWSS,IpWSS,PRWSS,RHOBWSS,PhiWSS]=SoftSandNew(Kf,RHOf,PhiSS,1-ClaySS,ClaySS,0,0,0,16,.4,CoordinationSand,1);	%	Porosity and clay range in shale	pSH1=min(b1(1,1),b2(1,1));	pSH2=max(b1(1,1),b2(1,1));	cSH1=min(b1(1,2),b2(1,2));	cSH2=max(b1(1,2),b2(1,2));%	Porosity and clay arrays in shale	pSH=[pSH1:(pSH2-pSH1)/20:pSH2]';	cSH=[cSH1:(cSH2-cSH1)/20:cSH2]';	[ClaySH PhiSH]=meshgrid(cSH,pSH);%	Wet Shale	[VpWSH,VsWSH,IpWSH,PRWSH,RHOBWSH,PhiWSH]=SoftSandNew(Kw,RHOw,PhiSH,1-ClaySH,ClaySH,0,0,0,16,.4,CoordinationShale,1);	%	AVO PLOTS	subplot('position',[.39 .11 .22 .37])	hold		subplot('position',[.71 .11 .22 .37])	hold    VpWSHVector=reshape(VpWSH,1,[])'; VsWSHVector=reshape(VsWSH,1,[])'; RHOBWSHVector=reshape(RHOBWSH,1,[])';    VpWSSVector=reshape(VpWSS,1,[])'; VsWSSVector=reshape(VsWSS,1,[])'; RHOBWSSVector=reshape(RHOBWSS,1,[])';    VpOSSVector=reshape(VpOSS,1,[])'; VsOSSVector=reshape(VsOSS,1,[])'; RHOBOSSVector=reshape(RHOBOSS,1,[])';	VpGSSVector=reshape(VpGSS,1,[])'; VsGSSVector=reshape(VsGSS,1,[])'; RHOBGSSVector=reshape(RHOBGSS,1,[])';%	AVO BETWEEN SHALE AND WET SAND	for i=1:15:length(VpWSHVector)		for j=1:15:length(VpWSSVector)			[Rpp,Rps,tD1,Intercept,Gradient]= ...			AVOZoeppritzNew(VpWSHVector(i),VpWSSVector(j),VsWSHVector(i),VsWSSVector(j),RHOBWSHVector(i),RHOBWSSVector(j));			subplot('position',[.39 .11 .22 .37]); plot(tD1,Rpp,'b-');			subplot('position',[.71 .11 .22 .37]); plot(Intercept,Gradient,'b*','linewidth',4);		end;    end;    %	AVO BETWEEN SHALE AND OIL SAND		for i=1:15:length(VpWSHVector)		for j=1:15:length(VpOSSVector)			[Rpp,Rps,tD1,Intercept,Gradient]= ...			AVOZoeppritzNew(VpWSHVector(i),VpOSSVector(j),VsWSHVector(i),VsOSSVector(j),RHOBWSHVector(i),RHOBOSSVector(j));			subplot('position',[.39 .11 .22 .37]); plot(tD1,Rpp,'k-');			subplot('position',[.71 .11 .22 .37]); plot(Intercept,Gradient,'k*','linewidth',4);		end;    end;%	AVO BETWEEN SHALE AND GAS SAND		for i=1:15:length(VpWSHVector)		for j=1:15:length(VpGSSVector)			[Rpp,Rps,tD1,Intercept,Gradient]= ...			AVOZoeppritzNew(VpWSHVector(i),VpGSSVector(j),VsWSHVector(i),VsGSSVector(j),RHOBWSHVector(i),RHOBGSSVector(j));			subplot('position',[.39 .11 .22 .37]); plot(tD1,Rpp,'r-');			subplot('position',[.71 .11 .22 .37]); plot(Intercept,Gradient,'r*','linewidth',4);		end;    end;	subplot('position',[.39 .11 .22 .37])	axis([0 40 -.4 .4]);	set(gca,'fontname','helvetica','fontsize',8);	set(gca,'xtick',[0:5:100]); set(gca,'ytick',[-20:.1:20]);	box on; grid on;	title('AVO ZOEPPRITZ','fontname','helvetica','fontsize',11,'color','k');	xlabel('ANGLE','fontname','helvetica','fontsize',8);	ylabel('R PP','fontname','helvetica','fontsize',8);	plot([0 60],[0 0],'k-','linewidth',1);	text(41,.35,'WET','fontsize',14,'fontweight','bold','color','b')	text(41,.28,'OIL','fontsize',14,'fontweight','bold','color','c')	text(41,.21,'GAS','fontsize',14,'fontweight','bold','color','r')				subplot('position',[.71 .11 .22 .37])	axis([-.4 .4 -.4 .4]);	set(gca,'fontname','helvetica','fontsize',8);	set(gca,'xtick',[-20:.1:20]); set(gca,'ytick',[-20:.2:20]);	box on; grid on;	title('GRADIENT VERSUS INTERCEPT','fontname','helvetica','fontsize',11,'color','k');	xlabel('INTERCEPT','fontname','helvetica','fontsize',8);	ylabel('GRADIENT','fontname','helvetica','fontsize',8);	plot([-1 1],[0 0],'k-','linewidth',1);	plot([0 0],[-1 1],'k-','linewidth',1);	text(.41,.35,'WET','fontsize',14,'fontweight','bold','color','b')	text(.41,.28,'OIL','fontsize',14,'fontweight','bold','color','c')	text(.41,.22,'GAS','fontsize',14,'fontweight','bold','color','r')	